            obj.IdVacuna = int.Parse(DropId);
            obj.IdProvincia = int.Parse(Drop2Id);

            Data.AddVacunado(obj);
            message = "Registro agregado con exito!";
        }
        catch (Exception)
        {

            message = "No se puedo completar operacion";
        }

    }
